mapper.select:
  	SELECT A.boardno, A.title, A.userid, A.content, A.hit, A.writedate, A.filename1,
  		   B.profileimage, B.userlevel
  	FROM   board A, member B
  	WHERE  A.userid=B.userid 
	AND    A.boardtype=${param1}
				order by no desclimit ${onePageRecord} offset ${offsetIndex}

SELECT boardno, title, userid, content, hit, writedate
FROM board
WHERE boardno = ${param1}

select boardno, title, userid, date_format(writedate,"%m/%d") writedate, boardtype
from board 
where boardtype=#{param2.boardtype}
order by boardno desc limit ${param1.recordPerPage} offset ${param1.offsetIndex}

select count(boardno) count 
from board
where boardtype=#{param2.boardtype}
